//load modules
var ytdl = require('ytdl');
var youtube = require('youtube-feeds');
var http = require('http');

// module vars
var current_search_page=1;
var current_start_index = 1;
var videos_responses = new Array();
var yt = {};


// search videos
yt.searchVideos = function (user_search, page, filters, cb){
    videos_responses = new Array();
    if (page === 1){
        current_start_index = 1;
    } else {
        current_start_index = page * 25 + 1;    
    }
    var params = {};
    if (filters === '') {
        params = {
                    'q':              ''+user_search+'',
                    'start-index' : ''+current_start_index+'',
                    'max-results':  25,
                    'orderby':        'relevance',
                }
    } else if (filters === 'hd') {
        params = {
                    'q':              ''+user_search+'',
                    'start-index' : ''+current_start_index+'',
                    'max-results':  25,
                    'orderby':        'relevance',
                    'hd' : true
                }
    } else if (filters === '3d') {
        params = {
                    'q':              ''+user_search+'',
                    'start-index' : ''+current_start_index+'',
                    'max-results':  25,
                    'orderby':        'relevance',
                    '3d' : true
                }
    }
    try{
        youtube.feeds.videos(params,
            function( err, data ) {
            if( err ) {
                cb(err);
            } else {
                cb(data);
            }
        });
    }catch(err){
        console.log('searchVideos err: '+err);
    }
}

